package de.itemis.mps.editor.celllayout.styles.behavior;

/*Generated by MPS */

import com.intellij.ui.components.OnOffButton;
import com.intellij.ui.scale.JBUIScale;
import java.util.Locale;
import java.awt.FontMetrics;
import java.awt.Insets;

public class MPSOnOffButton extends OnOffButton {

  @Override
  public int getBaseline(int width, int height) {
    int vGap = JBUIScale.scale(3);
    String text = (this.getOffText().length() > this.getOnText().length() ? this.getOffText() : this.getOnText());
    text = text.toUpperCase(Locale.getDefault());
    FontMetrics fm = this.getFontMetrics(this.getFont());
    Insets insets = getInsets();
    int textY = insets.top + vGap + fm.getAscent();
    return textY;
  }

}
