package de.itemis.mps.editor.celllayout.layout;

/*Generated by MPS */

import java.util.List;
import jetbrains.mps.internal.collections.runtime.ListSequence;

public class VerticalLayout extends AbstractGridLayout implements ILayouter {
  @Override
  protected Grid loadGrid(ILayoutableContainer container) {
    List<ILayoutable> children = container.getChildren();
    if (ListSequence.fromList(children).count() == 0) {
      return createGrid(0, 0);
    }
    Grid grid = createGrid(1, ListSequence.fromList(children).count());

    int y = 0;
    for (ILayoutable child : ListSequence.fromList(children)) {
      GridElement element = grid.getElement(0, y);
      element.setLayoutable(child);
      y++;
    }

    return grid;
  }

  @Override
  public boolean usesPunctuation() {
    return false;
  }
}
