package de.itemis.mps.editor.diagram.runtime.jgraph;

/*Generated by MPS */

import com.mxgraph.view.mxCellState;
import java.awt.Graphics2D;
import de.itemis.mps.editor.diagram.runtime.model.Bounds;
import java.awt.BasicStroke;

public class CheckboxButton extends ContextButton {

  private boolean myChecked = false;

  public CheckboxButton(double size, mxCellState state) {
    super(size, state);
  }

  public boolean isChecked() {
    return myChecked;
  }

  public void setChecked(boolean checked) {
    myChecked = checked;
  }

  @Override
  protected void paintSymbol(Graphics2D g) {
    if (isChecked()) {
      Bounds bounds = getBounds();
      bounds.expand(bounds.getWidth() * -0.2);

      g.setStroke(new BasicStroke((float) getLineWidth(), BasicStroke.CAP_ROUND, BasicStroke.JOIN_ROUND));
      drawLine(g, bounds.getLeftTop(), bounds.getRightBottom());
      drawLine(g, bounds.getRightTop(), bounds.getLeftBottom());
    }
  }

  @Override
  protected void execute() {
    setChecked(!(isChecked()));
    repaint();
  }
}
