package de.itemis.mps.editor.diagram.runtime.jgraph;

/*Generated by MPS */

import com.mxgraph.view.mxCellState;
import java.awt.Graphics2D;
import java.awt.BasicStroke;
import de.itemis.mps.editor.diagram.runtime.model.Bounds;

public class HideEdgeButton extends ContextButton {

  public HideEdgeButton(double size, mxCellState state) {
    super(size, state);
  }

  @Override
  protected void paintSymbol(Graphics2D g) {
    g.setColor(ContextButton.LINE_COLOR);
    g.setStroke(new BasicStroke((float) getLineWidth()));

    Bounds bounds = getBounds();
    // Calculate the start and end points for the vertical lines
    int leftX = bounds.getXInt() + (int) (bounds.getWidth() * 0.25);
    int rightX = bounds.getXInt() + (int) (bounds.getWidth() * 0.75);
    int startY = bounds.getYInt() + (int) (bounds.getHeight() * 0.25);
    int endY = bounds.getYInt() + (int) (bounds.getHeight() * 0.75);
    // Draw the left vertical line
    g.drawLine(leftX, startY, leftX, endY);
    // Draw the right vertical line
    g.drawLine(rightX, startY, rightX, endY);
    // Calculate the start and end points for the horizontal line
    int midY = bounds.getYInt() + (int) (bounds.getHeight() * 0.5);
    // Draw the horizontal line connecting the two vertical lines
    g.drawLine(leftX, midY, rightX, midY);
  }

  @Override
  protected void execute() {
    DiagramActionsUtil.hideEdge(getGraph(), getCell());
  }

  @Override
  public String getTooltip() {
    return "Hide Edge";
  }
}
