package de.itemis.mps.editor.diagram.runtime.jgraph;

/*Generated by MPS */

import com.mxgraph.view.mxCellState;
import java.awt.Graphics2D;
import java.awt.BasicStroke;
import de.itemis.mps.editor.diagram.runtime.model.Bounds;

public class ShowRootAllEdgesButton extends ContextButton {

  public ShowRootAllEdgesButton(double size, mxCellState state) {
    super(size, state);
  }

  @Override
  protected void paintSymbol(Graphics2D g) {
    g.setColor(ContextButton.LINE_COLOR);
    g.setStroke(new BasicStroke((float) getLineWidth()));

    Bounds bounds = getBounds();
    // Use getters instead of direct property access
    int startX = bounds.getXInt() + (int) (bounds.getWidth() * 0.25);
    int endX = bounds.getXInt() + (int) (bounds.getWidth() * 0.75);
    int startY = bounds.getYInt() + (int) (bounds.getHeight() * 0.25);
    int endY = bounds.getYInt() + (int) (bounds.getHeight() * 0.75);
    int midY = bounds.getYInt() + (int) (bounds.getHeight() * 0.5);
    // Draw the top half of the "S"
    g.drawArc(startX, startY, endX - startX, midY - startY, 90, 180);
    // Draw the bottom half of the "S"
    g.drawArc(startX, midY, endX - startX, endY - midY, 270, 180);

  }

  @Override
  protected void execute() {
    DiagramActionsUtil.toggleAllEdgesVisibility(getGraph(), null, true);
  }

  @Override
  public String getTooltip() {
    return "Show All Edges";
  }
}
