package de.itemis.mps.editor.diagram.runtime.jgraph;

/*Generated by MPS */

import com.mxgraph.view.mxCellState;
import java.awt.Graphics2D;
import de.itemis.mps.editor.diagram.runtime.model.Bounds;

public class SyncWithModelButton extends ContextButton {
  public SyncWithModelButton(double size, mxCellState state) {
    super(size, state);
  }

  @Override
  protected void paintSymbol(Graphics2D g) {
    Bounds bounds = getBounds();
    int centerX = (int) bounds.getCenterX();
    int centerY = (int) bounds.getCenterY();

    double arrowFraction = 0.2;
    int arrowLength = (int) (bounds.getWidth() * arrowFraction);

    g.drawLine(centerX, centerY - arrowLength, centerX, centerY + arrowLength);
    g.drawLine(centerX, centerY - arrowLength, centerX - arrowLength, centerY);
    g.drawLine(centerX - arrowLength, centerY, centerX, centerY + arrowLength);
    g.drawLine(centerX, centerY - arrowLength, centerX + arrowLength, centerY);
    g.drawLine(centerX + arrowLength, centerY, centerX, centerY + arrowLength);
  }

  @Override
  public String getTooltip() {
    return "Sync With Model";
  }

  @Override
  protected void execute() {
    DiagramActionsUtil.syncWithModel(getGraph());
  }
}
