package de.itemis.mps.editor.diagram.runtime.jgraph;

/*Generated by MPS */

import com.mxgraph.view.mxCellState;
import java.awt.Graphics2D;
import java.awt.BasicStroke;
import de.itemis.mps.editor.diagram.runtime.model.Bounds;
import de.itemis.mps.editor.diagram.runtime.model.Point;

public class ZoomOutButton extends ContextButton {

  public ZoomOutButton(double size, mxCellState state) {
    super(size, state);
  }


  @Override
  protected void paintSymbol(Graphics2D g) {
    g.setColor(LINE_COLOR);
    g.setStroke(new BasicStroke((float) getLineWidth()));

    Bounds bounds = getBounds();
    bounds.expand(bounds.getWidth() * -0.2);

    Point centerLeft = new Point(bounds.getX(), bounds.getCenterY());
    Point centerRight = new Point(bounds.getMaxX(), bounds.getCenterY());
    drawLine(g, centerLeft, centerRight);
  }


  @Override
  public String getTooltip() {
    return "Zoom out";
  }

  @Override
  protected void execute() {
    getGraph().getGraphComponent().zoomOut();
  }
}
