import jetbrains.mps.nodeEditor.EditorComponent;

public class ToggleLineNumbers_Action extends BaseAction {
  private static final Icon ICON = null;

  public ToggleLineNumbers_Action() {
    super("Toggle Line Numbers", "", ICON);
    this.setIsAlwaysVisible(false);
    this.setActionAccess(ActionAccess.UNDO_PROJECT);
    updateInBackground(true);
  }
  @Override
  public boolean isDumbAware() {
    return true;
  }
  @Override
  public void doUpdate(@NotNull AnActionEvent event, final Map<String, Object> _params) {
    // disable the action for version control components
    if (event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).getClass().getName().startsWith("jetbrains.mps.vcs")) {
      event.getPresentation().setEnabledAndVisible(false);
      return;
    }

    if (LineNumberPreferences.areLineNumbersEnabled(event.getData(CommonDataKeys.PROJECT))) {
      event.getPresentation().setText("Disable Line Numbers");
    } else {
      event.getPresentation().setText("Enable Line Numbers");
    }
  }
  @Override
  protected boolean collectActionData(AnActionEvent event, final Map<String, Object> _params) {
    if (!(super.collectActionData(event, _params))) {
      return false;
    }
    {
      Project p = event.getData(CommonDataKeys.PROJECT);
      if (p == null) {
        return false;
      }
    }
    {
      EditorComponent editorComponent = event.getData(MPSEditorDataKeys.EDITOR_COMPONENT);
      if (editorComponent != null && editorComponent.isInvalid()) {
        editorComponent = null;
      }
      if (editorComponent == null) {
        return false;
      }
    }
    return true;
  }
  @Override
  public void doExecute(@NotNull final AnActionEvent event, final Map<String, Object> _params) {
    boolean flag = LineNumberPreferences.areLineNumbersEnabled(event.getData(CommonDataKeys.PROJECT));
    LineNumberPreferences.setLineNumbersEnabled(event.getData(CommonDataKeys.PROJECT), !(flag));

    LineNumberComponent.uninstallAll();
    LineNumberComponent.installAll(event.getData(CommonDataKeys.PROJECT));
  }
}
