package com.mbeddr.mpsutil.projectview.runtime.tree.highlighter;

/*Generated by MPS */

import jetbrains.mps.project.Project;
import org.jetbrains.annotations.NotNull;
import com.mbeddr.mpsutil.projectview.runtime.tree.CustomTreeNode;
import org.jetbrains.mps.openapi.model.SModelReference;
import org.jetbrains.mps.openapi.model.SModel;
import java.util.List;
import java.util.ArrayList;
import jetbrains.mps.project.validation.ModelValidator;
import jetbrains.mps.project.ProjectBase;
import java.util.function.Consumer;
import jetbrains.mps.errors.item.ModelReportItem;
import jetbrains.mps.errors.MessageStatus;
import jetbrains.mps.progress.EmptyProgressMonitor;
import org.jetbrains.mps.openapi.module.SModuleReference;
import org.jetbrains.mps.openapi.module.SModule;
import jetbrains.mps.project.validation.MessageCollectProcessor;
import jetbrains.mps.errors.item.ModuleReportItem;
import jetbrains.mps.smodel.tempmodel.TempModule;
import jetbrains.mps.project.validation.ValidationUtil;
import jetbrains.mps.project.MPSProject;
import jetbrains.mps.ide.ui.tree.MPSTreeNode;
import java.util.Collections;

public class ErrorChecker extends TreeUpdateVisitor {
  public ErrorChecker(Project mpsProject) {
    super(mpsProject);
  }
  @Override
  public void visitModelNode(@NotNull final CustomTreeNode node) {
    final SModelReference mr = node.getModel().getReference();
    // FIXME ErrorChecker (as well as other participants of Tree Update process) has been reworked in MPS 2020.3!
    //      There are changes in messages propagation, as well as node update process.
    scheduleModelRead(node, new Runnable() {
      @Override
      public void run() {
        final SModel modelDescriptor = mr.resolve(myProject.getRepository());
        if (modelDescriptor == null || !(modelDescriptor.isLoaded())) {
          return;
        }
        final List<String> errors = new ArrayList<String>();
        final List<String> warnings = new ArrayList<String>();
        // FIXME just pass MPSProject into constructor to avoid cast to ProjectBase
        new ModelValidator(((ProjectBase) myProject).getPlatform(), modelDescriptor).validate(new Consumer<ModelReportItem>() {
          @Override
          public void accept(ModelReportItem problem) {
            if (problem.getSeverity() == MessageStatus.ERROR) {
              errors.add(problem.getMessage());
            } else {
              warnings.add(problem.getMessage());
            }
            // FIXME model validation could issue 'info'/'ok' messages, these would be recognized as 'warnings'!
          }
        }, new EmptyProgressMonitor());
        schedule(node, new ErrorReport(node, errors, warnings));
      }
    });
  }
  @Override
  public void visitModuleNode(@NotNull final CustomTreeNode node) {
    final SModuleReference mr = node.getModule().getModuleReference();
    scheduleModelRead(node, new Runnable() {
      @Override
      public void run() {
        SModule module = mr.resolve(myProject.getRepository());
        MessageCollectProcessor<ModuleReportItem> collector = new MessageCollectProcessor<ModuleReportItem>(true);
        if (module != null && !(module instanceof TempModule)) {
          ValidationUtil.validateModule(module, collector);
        }
        schedule(node, new ErrorReport(node, collector.getErrors(), collector.getWarnings()));
      }
    });
  }
  @Override
  public void visitProjectNode(@NotNull final CustomTreeNode node) {
    String errors = ((MPSProject) node.getObject(Project.class)).getErrors();
    addUpdate(node, new ErrorStateNodeUpdate(errors, false));
  }
  private class ErrorReport implements Runnable {
    private final MPSTreeNode myNode;
    private final List<String> errors;
    private final List<String> warns;
    public ErrorReport(MPSTreeNode node, List<String> errors, List<String> warns) {
      myNode = node;
      this.errors = errors;
      this.warns = (warns == null ? Collections.<String>emptyList() : warns);
    }
    @Override
    public void run() {
      StringBuilder result = new StringBuilder();
      if (errors.isEmpty() && warns.isEmpty()) {
        addUpdate(myNode, new ErrorStateNodeUpdate(null, true));
      } else {
        result.append("<html>");
        for (String error : errors) {
          result.append(error);
          result.append("<br>");
        }
        for (String warn : warns) {
          result.append("warn: ");
          result.append(warn);
          result.append("<br>");
        }
        addUpdate(myNode, new ErrorStateNodeUpdate(result.toString(), errors.isEmpty()));
      }
    }
  }
}
