public class DuplicateAction_Action extends BaseAction {
  private static final Icon ICON = null;

  public DuplicateAction_Action() {
    super("Duplicate Node", "", ICON);
    this.setIsAlwaysVisible(false);
    this.setActionAccess(EditorActionAccess.UNDO_EDITOR);
  }
  @Override
  public boolean isDumbAware() {
    return true;
  }
  @Override
  public void doUpdate(@NotNull final AnActionEvent event, final Map<String, Object> _params) {
    Presentation presentation = event.getPresentation();
    presentation.setEnabled(EditorActionUtils.isWriteActionEnabled(event.getData(MPSEditorDataKeys.EDITOR_COMPONENT), ListSequence.fromList(event.getData(MPSCommonDataKeys.NODES)).select((it) -> (EditorCell) event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).findNodeCell(it))));
    EditorCell selectedCell = event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).getSelectedCell();
    if (selectedCell instanceof EditorCell_Word) {
      presentation.setText("Duplicate Line");
    } else {
      presentation.setText("Duplicate Node2");
    }
  }
  @Override
  protected boolean collectActionData(AnActionEvent event, final Map<String, Object> _params) {
    if (!(super.collectActionData(event, _params))) {
      return false;
    }
    {
      EditorComponent editorComponent = event.getData(MPSEditorDataKeys.EDITOR_COMPONENT);
      if (editorComponent != null && editorComponent.isInvalid()) {
        editorComponent = null;
      }
      if (editorComponent == null) {
        return false;
      }
    }
    {
      List<SNode> nodes = event.getData(MPSCommonDataKeys.NODES);
      if (nodes == null) {
        return false;
      }
      if (nodes.isEmpty()) {
        return false;
      }

    }
    return true;
  }
  @Override
  public void doExecute(@NotNull final AnActionEvent event, final Map<String, Object> _params) {
    RichtextHelper helper = new RichtextHelper(event.getData(MPSEditorDataKeys.EDITOR_COMPONENT));
    if ((helper.getTextNode() != null)) {
      TextModificationUtil.duplicateCurrentLine(event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).getEditorContext());
    } else {
      ActionUtils.updateAndPerformAction(new CopyThisDown_Action(), ActionUtils.createEvent(ActionPlaces.UNKNOWN, DataManager.getInstance().getDataContext(event.getData(MPSEditorDataKeys.EDITOR_COMPONENT))));
    }
  }
}
