package de.q60.mps.shadowmodels.runtime.util;

/*Generated by MPS */

import java.security.MessageDigest;
import java.util.Base64;

public class IdShortener {

  public static String shorten(String longId, int length) {
    try {
      MessageDigest md = MessageDigest.getInstance("SHA-256");
      md.update(longId.getBytes("UTF-8"));
      return Base64.getUrlEncoder().withoutPadding().encodeToString(md.digest()).substring(0, length);
    } catch (Exception ex) {
      return longId;
    }
  }

  public static String shorten(String longId) {
    return shorten(longId, 43);
  }

}
