package nl.f1re.mps.editor.swing.runtime;

/*Generated by MPS */

import com.intellij.util.ui.JBFont;
import jetbrains.mps.nodeEditor.EditorSettings;
import com.intellij.openapi.util.SystemInfo;
import com.intellij.ui.NewUiValue;

/**
 * This class serves the same purpose as JBFont but uses the default editor font instead of the standard font for labels.
 */
public class FontHelper {

  public static JBFont getDefaultFont() {
    return JBFont.create(EditorSettings.getInstance().getDefaultEditorFont());
  }

  public static JBFont h0() {
    return getDefaultFont().biggerOn(12).asBold();
  }

  public static JBFont h1() {
    return getDefaultFont().biggerOn(9).asBold();
  }

  public static JBFont h2() {
    return getDefaultFont().biggerOn(5);
  }

  public static JBFont h3() {
    return getDefaultFont().biggerOn(3);
  }

  public static JBFont h4() {
    return getDefaultFont().biggerOn(1).asBold();
  }

  public static JBFont regular() {
    return getDefaultFont();
  }

  public static JBFont medium() {
    return (mediumAndSmallFontsAsRegular() ? getDefaultFont() : getDefaultFont().lessOn(1));
  }

  public static JBFont small() {
    return (mediumAndSmallFontsAsRegular() ? getDefaultFont() : getDefaultFont().lessOn(2));
  }

  public static JBFont verySmall() {
    return (mediumAndSmallFontsAsRegular() ? getDefaultFont() : getDefaultFont().lessOn(3));
  }

  private static boolean mediumAndSmallFontsAsRegular() {
    return SystemInfo.isWindows && !(NewUiValue.isEnabled());
  }
}
